import * as React from "react";
import Box from "@mui/material/Box";
import Button from "@mui/material/Button";
import Dialog from "@mui/material/Dialog";
import DialogActions from "@mui/material/DialogActions";
import DialogContent from "@mui/material/DialogContent";
import DialogContentText from "@mui/material/DialogContentText";
import DialogTitle from "@mui/material/DialogTitle";
import CardMedia from "@mui/material/Card";
import Card from "@mui/material/Card";
import Typography from '@mui/material/Typography';
import Avatar from '@mui/material/Avatar';
import { styled } from '@mui/material/styles';
import { red } from '@mui/material/colors';
export default function ViewImage(props) {
    //set Properties and get item style details;
  const [open, setOpen] = React.useState(false);
  const [itemDetail,setItemDetail]=React.useState({});
  const handleClickOpen = () => {

     fetch(`http://52.26.193.201:3000/products/3/styles`)
     .then(response=>response.json()).then(json=>setItemDetail(json));
     setOpen(true);


  };

  const handleClose = () => {

    setOpen(false);
  };
  console.log("item : ",(itemDetail.results?itemDetail.results[0].photos[0].url:1));

  return (
    <React.Fragment>
      <Button variant="outlined" onClick={handleClickOpen}>
       View Details
      </Button>
      <Dialog fullWidth={false} open={open} onClose={handleClose}>
        <DialogTitle>Optional sizes</DialogTitle>
        <DialogContent>
          <DialogContentText></DialogContentText>

          <Card sx={{ maxWidth: 345 }}>

                <CardMedia
                component="img"
                height="194"
                image="/static/images/cards/paella.jpg"
                alt="Paella dish"
                />
                <CardContent>
                <Typography variant="body2" color="text.secondary">
                    This impressive paella is a perfect party dish and a fun meal to cook
                    together with your guests. Add 1 cup of frozen peas along with the mussels,
                    if you like.
                </Typography>
                </CardContent>

            </Card>
          {/* <Avatar sx={{ bgcolor: red[500] }} aria-label="recipe">
            ${itemDetail.results?itemDetail.price:0}
          </Avatar> */}
        </DialogContent>
        <DialogActions>
          <Button onClick={handleClose}>Close</Button>
        </DialogActions>
      </Dialog>
    </React.Fragment>
  );
}
